#define _CRT_SECURE_NO_WARNINGS	
#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <malloc.h>
#define N 50
#define M 224
typedef enum { FALSE, TRUE }bool;

typedef struct Tank
{
	char name;
	bool live;
	int life;
	int x;
	int y;

}Tank;

Tank ** initialize(int players);
void Play(char Board[N][M], Tank** Arr, int players);
void PrintBoard(char Board[N][M]);
void Move(Tank* tank, char Board[N][M]);
void Shoot(Tank* tank, char Board[N][M]);
void Directshot(Tank* tank, char Board[N][M]);
char ** BuildMap();
int main()
{
	char** GameBoard = (char**)malloc(N * sizeof(char*));
	for (int i = 0; i < N; i++)
	{
		GameBoard[i] = (char*)malloc(M * sizeof(char));
	}
	//char GameBoard[50][224];
	GameBoard = BuildMap();
	PrintBoard(GameBoard);
	int players;
	Tank ** Arr = NULL;
	printf("Enter number of players\n");
	scanf("%d", &players);
	Arr = initialize(players);
	for (int i = 0; i < players; i++)
		GameBoard[Arr[i]->x][Arr[i]->y] = Arr[i]->name;
	Play(GameBoard, Arr, players);
	PrintBoard(GameBoard);
	for (int i = 0; i < players; i++) // free memory
		free(Arr[i]);
	free(Arr);
	
	
	return 0;
}
