void PrintBoard(char Board[N][M])
{
	for (int i = 0; i < 50; i++)
	{
		for (int j = 0; j < 224; j++)
			printf("%c", Board[i][j]);
	}
}
Tank ** initialize(int players)
{
	char c = 'A';
	Tank ** Arr = (Tank*)malloc((sizeof(Tank))*players);
	for (int i = 0; i < players; i++, c++)
	{
		Arr[i] = (Tank*)malloc(sizeof(Tank));
		Arr[i]->live = TRUE;
		Arr[i]->life = 2;
		Arr[i]->name = c;
		time_t t;
		srand((unsigned)time(&t));
		Arr[i]->x = i + 2;
		Arr[i]->y = i + 3;
		//rand() % N;
	}

	return Arr;
}
void Play(char Board[N][M], Tank** Arr, int players)
{
	int count = players, choose;
	while (count > 1)
	{
		for (int i = 0; i < players; i++)
			if (Arr[i]->live == TRUE)
			{
				printf("Its player %d turn!\n", i + 1);
				printf("would you like to move?1-Yes,0-No\t");
				scanf("%d", &choose);
				if (choose)
					Move(Arr[i], Board);
				Shoot(Arr[i], Board);
			}
	}
}
void Move(Tank* tank, char Board[N][M])
{
	PrintBoard(Board);
	int choose, step;
	Board[tank->x][tank->y] = '-';
	printf("What direction you want to move? Right-1, Left-2\t");
	scanf("%d", &choose);
	printf("Enter how many steps do you want to move\t");
	scanf("%d", &step);
	if (choose == 2)
		step = step*(-1);
	if (tank->y + step >= N)
		tank->y = N - 1;
	else if (tank->y + step < 0)
		tank->y = 0;
	else
		tank->y = tank->y + step;
	if (Board[tank->x][tank->y] == '-')
		while (Board[tank->x + 1][tank->y] == '-')
			tank->x = tank->x + 1;
	else if (Board[tank->x][tank->y] != '-')
		while (Board[tank->x - 1][tank->y] != '-')
			tank->x = tank->x - 1;
	Board[tank->x][tank->y] = tank->name;
	PrintBoard(Board);
}
void Shoot(Tank* tank, char Board[N][M])
{
	int choose;
	printf("Enter what kind of shoot do you want:\n1.Direct shot.\n2.Steep path\n3.Guided Missile.\n4.Crazy sheep.\n");
	scanf("%d", &choose);
	switch (choose)
	{
	case 1:
		Directshot(tank, Board);

		break;
	}
}
